add_filter( 'frm_validate_field_entry', 'block_urls_emails_in_textareas', 10, 3 );
function block_urls_emails_in_textareas( $errors, $posted_field, $posted_value ) {
	if ( $posted_field->type === 'textarea' ) {
		$pattern = '/(https?:\/\/[^\s]+|www\.[^\s]+|[\w.-]+@[\w.-]+\.[a-zA-Z]{2,}|(?:[a-zA-Z0-9-]+\.)+[a-zA-Z]{2,})/i';

		if ( preg_match( $pattern, $posted_value ) ) {
			$errors['field' . $posted_field->id] = 'Please do not include email addresses, website URLs, or domain names.';
		}
	}
	return $errors;
}

add_filter( 'frm_validate_field_entry', 'block_non_latin_in_textareas', 10, 3 );
function block_non_latin_in_textareas( $errors, $posted_field, $posted_value ) {
	if ( $posted_field->type === 'textarea' ) {
		$allowed_pattern = '/^[\x00-\x7F\xA0-\xFF¡¿“”‘’"\'(),.!?;:\-\n\r]*$/u';

		if ( ! preg_match( $allowed_pattern, $posted_value ) ) {
			$errors['field' . $posted_field->id] = 'Only English and Spanish characters are allowed.';
		}
	}
	return $errors;
}

add_filter( 'frm_validate_field_entry', 'block_spammy_email_domains_by_type', 10, 3 );
function block_spammy_email_domains_by_type( $errors, $posted_field, $posted_value ) {
	if ( $posted_field->type === 'email' ) {
		$blocked_patterns = [
			'@.*\.ru$',
			'@.*\.cn$',
			'@.*\.xyz$',
			'@.*\.top$',
			'@.*\.tk$',
			'@.*\.ml$',
			'@.*\.ga$',
			'@.*\.cf$',
			'@.*\.gq$',
			'@.*\.club$',
			'@.*\.click$',
			'@.*\.info$',
			'@.*\.biz$',
			'@gmx\.com$',
			'@gmx\.net$',
			'@mail\.ru$',
			'xruma\.store',
			'bubuk\.site',
			'@yandex\.ru$',
			'@protonmail\.com$',
			'@znemail\.com$',
			'@jmailservice\.com$'
		];

		foreach ( $blocked_patterns as $pattern ) {
			if ( preg_match( "/$pattern/i", $posted_value ) ) {
				$errors['field' . $posted_field->id] = 'Email addresses from this domain are not allowed.';
				break;
			}
		}
	}
	return $errors;
}

add_filter( 'frm_validate_field_entry', 'block_excessive_dots_in_email', 10, 3 );
function block_excessive_dots_in_email( $errors, $posted_field, $posted_value ) {
	if ( $posted_field->type === 'email' ) {
		$email_parts = explode('@', $posted_value);
		if ( count($email_parts) === 2 ) {
			$local = $email_parts[0];
			$dot_count = substr_count($local, '.');

			if ( $dot_count >= 5 ) {
				$errors['field' . $posted_field->id] = 'That email address format is not allowed.';
			}
		}
	}
	return $errors;
}

/* insert <span class="email-obfuscated" data-user="example-name" data-domain="exampledomain.com"></span> instead of email a tag */
document.addEventListener("DOMContentLoaded", function () {
    const elements = document.querySelectorAll(".email-obfuscated");

    elements.forEach(el => {
      const user = el.getAttribute("data-user");
      const domain = el.getAttribute("data-domain");
      const email = `${user}@${domain}`;

      const link = document.createElement("a");
      link.href = `mailto:${email}`;
      link.textContent = email;

      el.replaceWith(link);
    });
});

